Sales <- data.frame(tv_ad,radio_ad,news_ad,sales) 
ggpairs(Sales, lower = list(continuous="points",combo="facetdensity"))
#model, one predictor is not significant, interaction between continuous predictors, anova to compare nested models

sales.lm1 <- lm(formula = sales ~ tv_ad + radio_ad + news_ad, data = Sales) 
summary(sales.lm1)

sales.lm1=update(sales.lm1,.~.-news_ad)
range_tv <- range(Sales$tv_ad) 
new_values_tv <- seq(from = range_tv[1], to = range_tv[2], length.out = 20) 
range_radio <- range(Sales$radio_ad) 
new_values_radio <- seq(from = range_radio[1], to = range_radio[2], length.out = 20) 
predictions <- outer(X = new_values_tv, Y = new_values_radio, FUN = function(tv_ad, radio_ad) 
  {predict(object = sales.lm1, newdata = data.frame(tv_ad, radio_ad)) }) 
surface <- persp(x = new_values_tv, y = new_values_radio, z = predictions, theta = 18, phi = 20, col = "lightblue", shade = 0.1, xlab = "tv_ad", ylab = "radio_ad", zlab = "sales", ticktype = "detailed", main = "Sales prediction ~ TV and Radio") 
observations <- trans3d(Sales$tv_ad, Sales$radio_ad, Sales$sales, surface) 
error <- trans3d(Sales$tv_ad, Sales$radio_ad, fitted(sales.lm1), surface) 
points(observations, col = "red", pch = 16) 
segments(observations$x, observations$y, error$x, error$y)

# model with an interaction
sales.lm1.int <- lm(formula = sales ~ tv_ad + radio_ad + tv_ad:radio_ad, data = Sales) 
summary(sales.lm1.int)
anova(sales.lm1,sales.lm1.int)

range_tv <- range(Sales$tv_ad) 
new_values_tv <- seq(from = range_tv[1], to = range_tv[2], length.out = 20) 
range_radio <- range(Sales$radio_ad) 
new_values_radio <- seq(from = range_radio[1], to = range_radio[2], length.out = 20) 
predictions <- outer(X = new_values_tv, Y = new_values_radio, FUN = function(tv_ad, radio_ad) 
{predict(object = sales.lm1.int, newdata = data.frame(tv_ad, radio_ad)) }) 
surface <- persp(x = new_values_tv, y = new_values_radio, z = predictions, theta = 18, phi = 20, col = "lightblue", shade = 0.1, xlab = "tv_ad", ylab = "radio_ad", zlab = "sales", ticktype = "detailed", main = "Sales prediction ~ TV and Radio") 
observations <- trans3d(Sales$tv_ad, Sales$radio_ad, Sales$sales, surface) 
error <- trans3d(Sales$tv_ad, Sales$radio_ad, fitted(sales.lm1.int), surface) 
points(observations, col = "red", pch = 16) 
segments(observations$x, observations$y, error$x, error$y)

